mapper.select:
SELECT b.deployunitname,a.* FROM api a inner join deployunit b on a.deployunitid=b.id
a.apiname like CONCAT('%',#{apiname},'%') and a.modelname like CONCAT('%',#{modelname},'%') and a.deployunitname like CONCAT('%',#{deployunitname},'%') and a.projectid =#{projectid}
order by lastmodify_time DESC

SELECT a.*
FROM api a

  a.deployunitid =#{deployunitid}
  and a.modelid =#{modelid}

SELECT a.*
FROM api a
where a.deployunitname=#{deployunitname} and a.apiname=#{apiname}

SELECT a.*
FROM api a
where a.deployunitid=#{deployunitid} and a.visittype=#{visittype} and a.path=#{path}
