mapper.select:
SELECT a.*
FROM apicases a

  a.apiname = #{apiname}
  and a.casetype = #{casetype}
  and a.deployunitname = #{deployunitname}
  and a.casename like CONCAT('%',#{casename},'%')

order by lastmodify_time  DESC

SELECT a.*
FROM apicases a

  a.apiname = #{apiname}
  and a.deployunitname = #{deployunitname}
  and a.casetype = #{casetype}

SELECT id,apiname,deployunitname,casename,casejmxname,casecontent,casetype,expect,level,memo,create_time,lastmodify_time,creator FROM apicases
WHERE deployunitname = #{deployunitname} and apiname = #{apiname}
and id != #{id} and casename = #{casename}

mapper.update:
update apicases set casename = #{casename},casejmxname = #{casejmxname}, apiname = #{apiname}, deployunitname = #{deployunitname}, casecontent = #{casecontent},expect = #{expect},
memo = #{memo},casetype=#{casetype},threadnum=#{threadnum},loops=#{loops},middleparam=#{middleparam},level=#{level}, creator = #{creator},lastmodify_time = DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s')
where id = #{id};
