mapper.select:
SELECT a.*, b.executeplanname
FROM executeplan_testcase a join executeplan b on a.executeplanid=b.id

  a.deployunitid = #{deployunitid} 
  and a.executeplanid = #{executeplanid}
  and a.apiid = #{apiid}

order by lastmodify_time  DESC

select testcaseid from executeplan_testcase c

  and c.deployunitname like CONCAT('%',#{deployunitname},'%')
  and c.apiname like CONCAT('%',#{apiname},'%') and c.executeplanid = #{executeplanid}
